<template>
  <div id="nav">
    <div>
      <h1>CoderQuiz</h1>
    </div>
    <div class="nav-items">
      <div
        class="click-blocker"
        v-if="isOpen.some((e) => e)"
        @click="setOpenDropdown('all', false)"
      />
      <ButtonDropdown :isOpen="isOpen[0]">
        <template v-slot:btn>
          <a @click="setOpenDropdown(0)">Quiz</a>
        </template>
        <template v-slot:dropdown>
          <router-link
            :to="{ name: 'Play' }"
            @click="setOpenDropdown('all', false)"
          >
            Play
          </router-link>
          <router-link
            :to="{ name: 'Leaderboard' }"
            @click="setOpenDropdown('all', false)"
          >
            Ranks
          </router-link>
        </template>
      </ButtonDropdown>
      <router-link :to="{ name: 'About' }">About</router-link>
      <ButtonDropdown :isOpen="isOpen[1]">
        <template v-slot:btn>
          <div v-if="$cookies.isKey('jwt')">
            <a @click="setOpenDropdown(1)">User</a>
          </div>
          <div v-else>
            <a @click="setOpenDropdown(1)">Login</a>
          </div>
        </template>
        <template v-slot:dropdown>
          <div v-if="$cookies.isKey('jwt')">
            <UserProfile />
            <a href="/auth/logout" @click="setOpenDropdown('all', false)">
              Logout
            </a>
          </div>
          <div v-else>
            <a href="/auth/login" @click="setOpenDropdown('all', false)">
              Login with GitHub
            </a>
          </div>
        </template>
      </ButtonDropdown>
    </div>
  </div>
  <router-view />
</template>

<script>
import ButtonDropdown from "./components/ButtonDropdown";
import UserProfile from "./components/UserProfile";

export default {
  name: "App",
  components: {
    ButtonDropdown,
    UserProfile,
  },
  data() {
    return {
      isOpen: [false, false],
    };
  },
  methods: {
    setOpenDropdown(n, isOpen = null) {
      this.isOpen = this.isOpen.map(() => false);
      if (n == "all") {
        this.isOpen = this.isOpen.map(() => isOpen);
      } else if (isOpen !== null) {
        this.isOpen = this.isOpen.map((_, i) => (n == i ? isOpen : false));
      } else {
        this.isOpen = this.isOpen.map((_, i) => (n == i ? !isOpen : false));
      }
    },
  },
};
</script>

